pragma solidity 0.5.16;

import "../@openzeppelin/contracts-ethereum-package/contracts/token/ERC20/ERC20.sol";
import "../roles/RevokerRole.sol";

contract RevocableTo is ERC20, RevokerRole {

  event RevokeTo(address indexed revoker, address indexed from, address indexed to, uint256 amount);

  function _revokeTo(
    address _from,
    address _to,
    uint256 _amount
  )
    internal
    returns (bool)
  {
    ERC20._transfer(_from, _to, _amount);
    emit RevokeTo(msg.sender, _from, _to, _amount);
    return true;
  }

  /**
  Allow Admins to revoke tokens from any address to any destination
    */
  function revokeTo(address from, address to, uint256 amount) public onlyRevoker returns (bool) {
      return _revokeTo(from, to, amount);
  }
}
